mapper.insert:
insert into course
values ( #{course.className}, #{course.name}, #{course.startTime}, #{course.endTime}
       , #{course.ownerId}, #{course.code}, #{course.personNum}, #{course.isMix}, #{course.ownerName},#{course.season});
insert into creat_course (accountName,code,isTop,time)
values (#{course.ownerId}, #{course.code}, #{course.isTop},#{time});

mapper.update:
update join_course
set isTop=1
where accountName = #{accountName}
  and code = #{code};
update creat_course
set isTop=1
where accountName = #{accountName}
  and code = #{code};

update join_course
set isTop=0
where accountName = #{accountName}
  and code = #{code};
update creat_course
set isTop=0
where accountName = #{accountName}
  and code = #{code};

        #       删除二者的联系
        delete
        from join_course
        where accountName = #{accountName}
          and code = #{code};
#         课程加入人数减一
        update course
        set personNum=personNum - 1
        where code = #{code};
#         移除作业联系
        delete
        from student_work
        where accountName = #{accountName}
          and code = #{code};

mapper.select:
select isTop
from join_course where code=#{code} and accountName=#{accountName};

update join_course set is_guidang = false
where code = #{code};

select isTop
from creat_course where code=#{code} and accountName=#{accountName};

update creat_course set is_guidang = false
where code = #{code};

select accountName
from join_course
where code = #{code};

select id
from student_work
where code = #{code};
