mapper.select:
select *
from course c
 join creat_course cc on c.code=cc.code and cc.is_guidang=#{isGuiDang}
where ownerId = #{ownerId};

select *
from course
where code in (select code
               from join_course
               where accountName = #{accountName}
                 and isTop and !is_guidang)
   or code in (select code
               from creat_course
               where accountName = #{accountName}
                 and isTop and !is_guidang);
